INSERT INTO `uzbemb_headlines` VALUES (1,'2014-12-12','2015-02-27','0000-00-00',0,1,'','','&amp;lt;p&amp;gt;Selamat datang di website resmi baru dari Kedutaan Besar Republik Uzbekistan di Jakarta.&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;Kepada para pengunjung yang terhormat,&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp;&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;Selamat datang di situs web resmi baru Kedutaan Besar Republic Indonesia di Jakarta.&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;Kami berharap situs web ini dapat membantu anda untuk memperkaya pengetahuan dan pemahaman tentang Uzbekistan, kehadirannya di Republik Indonesia, dan hubungan bilateral antara kedua negara.&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;Situs web ini dirancang untuk memberikan informasi tentang berbagai topik tentang kerja sama antara Uzbekistan dan Indonesia, interaksi ekonomi keduanya serta berita dan siaran pers mengenai ekonomi dan bisnis Uzbekistan, budaya dan pariwisata, pendidikan dan sains.&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;Kami harap situs web kami dapat memberikan anda kunjungan yang menarik dan informatif&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp;&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;Salam hangat,&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;Kedutaan Besar Republik Uzbekistan&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp;&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp;&amp;lt;/p&amp;gt;','','','&amp;lt;p&amp;gt;&amp;lt;span style=&amp;quot;font-size:14pt&amp;quot;&amp;gt;Dear our visitor,&amp;lt;/span&amp;gt;&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp;&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;Welcome to the new official website of the Embassy of the Republic of Uzbekistan in Jakarta.&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;&amp;lt;br /&amp;gt;\r\nWe hope this website will help you to enhance your knowledge and understanding about Uzbekistan, its presence in the Republic of Indonesia, and bilateral relations between our countries.&amp;lt;br /&amp;gt;\r\n&amp;lt;br /&amp;gt;\r\nWebsite has been designed to provide information on a broad range of topics about co-operation between Uzbekistan and Indonesia, their economic interaction as well as news and press releases on Uzbekistan economy and business, culture and tourism, education and science.&amp;lt;br /&amp;gt;\r\n&amp;amp;nbsp;&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;We wish you an interesting and informative visit to our website&amp;amp;nbsp;&amp;lt;br /&amp;gt;\r\n&amp;lt;br /&amp;gt;\r\n&amp;lt;strong&amp;gt;Warm Regards,&amp;lt;/strong&amp;gt;&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;Embassy of the Republic of Uzbekistan&amp;lt;/p&amp;gt;','','','&amp;lt;p&amp;gt;Sahifamizning aziz mehmonlari,&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp;&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp; &amp;amp;nbsp; &amp;amp;nbsp;O&amp;amp;rsquo;zbekiston Respublikasining Jakarta shahridagi elchixonasining yangi web-sahifasiga xush kelibsiz.&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp;&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp; &amp;amp;nbsp; &amp;amp;nbsp; Web-sahifamiz sizni O&amp;amp;rsquo;zbekiston mamlakati, elchixonamizning Indoneziya Respublikasidagi faoliyati hamda davlatlarimiz o&amp;amp;rsquo;rtasidagi ikki tomonlama munosabatlar haqida o&amp;amp;rsquo;z bilimlaringizni oshirishda yordam beradi degan umiddamiz.&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp;&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp; &amp;amp;nbsp; &amp;amp;nbsp;Ushbu web-sahifada O&amp;amp;rsquo;zbekiston va Indoneziya o&amp;amp;rsquo;rtasidagi iqtisodiy va madaniy hamkorlik, mamlakat hayotidagi yangiliklar, O&amp;amp;rsquo;zbekiston iqtisodiyoti va tadbirkorlik, madaniyat va turizm, ta&amp;amp;rsquo;lim, ilm- fan va boshqa mavzudagi ma&amp;amp;rsquo;lumotlar bilan boitilgan.&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp;&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp; &amp;amp;nbsp; &amp;amp;nbsp; Sahifamizga tashrifingiz qiziqarli va sermazmun bo&amp;amp;rsquo;lishini umid qilib qolamiz.&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp;&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;&amp;amp;nbsp; &amp;amp;nbsp; &amp;amp;nbsp; &amp;amp;nbsp;Yuksak ehtirom ila,&amp;amp;nbsp;&amp;lt;/p&amp;gt;\r\n\r\n&amp;lt;p&amp;gt;O&amp;amp;rsquo;zbekiston Respublikasining elchixonasi&amp;amp;nbsp;&amp;lt;/p&amp;gt;','',NULL,NULL,NULL,NULL,'','',NULL,NULL,'',NULL,NULL,NULL,NULL,0);
/*!40000 ALTER TABLE `uzbemb_headlines` ENABLE KEYS */;
